import * as express from 'express';
import Collection, { Collectable } from './collection';

interface IRoute {
  name: string,
  path: string,

  get?(req:express.Request, res:express.Response): void,
  post?(req:express.Request, res:express.Response): void
};

export default class Route implements IRoute {
  name:string;
  path:string = '/';

  children:Collection<Route> = new Collection<Route>();
  middlewares:void[] = [];

  constructor(path:string, name:string) {
    this.path = path;
    this.name = name;
  }
}

export { IRoute };
